import BodyText from '@enact/sandstone/BodyText';
import { Header, Panel } from '@enact/sandstone/Panels';
import TabLayout, { Tab } from '@enact/sandstone/TabLayout';
import { useMainState } from './MainState';
import SelectableVideoPlayer from './VideoPlayer';
import Scroller from '@enact/sandstone/Scroller';
import ImageItem from '@enact/sandstone/ImageItem'; // ImageItem import
import css from './Main.module.less';
import $L from '@enact/i18n/$L';
import { useState, useEffect } from 'react';
import { svgGenerator } from '../utils/svgGenerator'; // svgGenerator import

const Main = (props) => {
    const { videoData } = useMainState();
    const [selectedVideo, setSelectedVideo] = useState(null);
    const [activeTab, setActiveTab] = useState('watching');

    useEffect(() => {
        console.log(videoData);
    }, [videoData]);

    const handleVideoClick = (video) => {
        setSelectedVideo(video);
    };

    const handleClosePlayer = () => {
        setSelectedVideo(null);
    };

    const renderImageItem = (video, index) => (
        <ImageItem
            inline
            key={video.id}
            label={video.title} // 비디오 제목을 레이블로 사용
            src={video.thumbnail || svgGenerator(360, 240, 'd8d8d8', '6e6e6e', '360 X 240')} // 썸네일 소스
            style={{
                width: '100%', // 너비를 100%로 설정
                height: 'auto' // 높이는 자동으로 설정
            }}
            onClick={() => handleVideoClick(video)} // 클릭 시 비디오 선택
        >
            {`ImageItem ${index + 1}`}
        </ImageItem>
    );

    return (
        <Panel {...props}>
            {selectedVideo && (
                <SelectableVideoPlayer video={selectedVideo} onClose={handleClosePlayer} />
            )}

            <Scroller
                direction="vertical"
                style={{ height: '100vh', overflowY: 'auto' }}
            >
                <Header title={$L('YouTube Clone')} />
                <BodyText className={css.description} size="large">
                    {$L('이곳은 유튜브 메인 화면을 모방한 샘플 애플리케이션입니다.')}
                </BodyText>

                <TabLayout
                    selected={activeTab}
                    onSelect={(tabId) => {
                        console.log('Selected Tab:', tabId);
                        const selectedTabKey = ['watching', 'recommended', 'additional'][tabId.index];
                        setActiveTab(selectedTabKey);
                    }}
                    orientation="horizontal"
                >
                    <Tab title={$L('시청 중인 영상')}>
                        <Scroller
                            direction="vertical"
                            style={{ height: '400px', overflowY: 'auto' }}
                        >
                            <div className={css.videoGrid}>
                                {videoData.length > 0 ? videoData.slice(0, 5).map(renderImageItem) : <BodyText>{'비디오가 없습니다.'}</BodyText>}
                            </div>
                        </Scroller>
                    </Tab>
                    <Tab title={$L('추천 영상')}>
                        <Scroller
                            direction="vertical"
                            style={{ height: '400px', overflowY: 'auto' }}
                        >
                            <div className={css.videoGrid}>
                                {videoData.length > 5 ? videoData.slice(5, 10).map(renderImageItem) : <BodyText>{'비디오가 없습니다.'}</BodyText>}
                            </div>
                        </Scroller>
                    </Tab>
                    <Tab title={$L('추가 섹션')}>
                        <Scroller
                            direction="vertical"
                            style={{ height: '400px', overflowY: 'auto' }}
                        >
                            <BodyText className={css.description}>
                                {Array(20).fill('이곳은 추가 섹션입니다. 더 많은 내용을 추가해 주세요.').map((text, index) => (
                                    <div key={index}>{text}</div>
                                ))}
                            </BodyText>
                        </Scroller>
                    </Tab>
                </TabLayout>
            </Scroller>
        </Panel>
    );
};

export default Main;
