import { useState } from 'react';
import Login from '../views/Login';
import Signup from '../views/Signup';
import Main from '../views/Main';

const App = () => {
    const [currentPage, setCurrentPage] = useState('login'); // 'login', 'signup', 'main' 중 하나

    const handleLoginClose = () => {
        setCurrentPage('main'); // 로그인 화면 종료 후 메인 화면으로 이동
    };

    const handleGoToLogin = () => {
        setCurrentPage('login'); // 회원가입에서 로그인 화면으로 이동
    };

    const handleGoToSignup = () => {
        setCurrentPage('signup'); // 로그인에서 회원가입 화면으로 이동
    };

    const handleSignup = (username, password) => {
        // 회원가입 처리 로직
        console.log(`New User: ${username}, ${password}`);
        setCurrentPage('login'); // 회원가입 완료 후 로그인 화면으로 이동
    };

    return (
        <div>
            {currentPage === 'login' && (
                <Login
                    onClose={handleLoginClose}
                    onSignupClick={handleGoToSignup} // 회원가입 화면으로 이동
                />
            )}
            {currentPage === 'signup' && (
                <Signup
                    onLoginClick={handleGoToLogin} // 로그인 화면으로 이동
                    onSignup={handleSignup} // 회원가입 처리
                />
            )}
            {currentPage === 'main' && <Main />}
        </div>
    );
};

export default App;
