// src/App/Login.js
import { useState } from 'react';
import BodyText from '@enact/sandstone/BodyText';
import Button from '@enact/sandstone/Button';
import { Header, Panel } from '@enact/sandstone/Panels';
import css from '../views/Main.module.less'; // CSS 파일 경로 확인
import $L from '@enact/i18n/$L';

const Login = ({ onLogin }) => {
	const [username, setUsername] = useState('');
	const [password, setPassword] = useState('');

	const handleLogin = () => {
		if (username && password) {
			onLogin(); // 로그인 성공 시 메인 화면으로 이동
		}
	};

	const handleChangeUsername = (e) => setUsername(e.target.value);
	const handleChangePassword = (e) => setPassword(e.target.value);

	return (
		<Panel>
			<Header title={$L('로그인')} />
			<BodyText>{$L('사용자 이름과 비밀번호를 입력하세요.')}</BodyText>
			<div className={css.loginContainer}>
				<input
					type="text"
					placeholder="사용자 이름"
					value={username}
					onChange={handleChangeUsername} // 일반 함수로 변경
				/>
				<input
					type="password"
					placeholder="비밀번호"
					value={password}
					onChange={handleChangePassword} // 일반 함수로 변경
				/>
				<Button onClick={handleLogin} size="small">
					{$L('로그인')}
				</Button>
			</div>
		</Panel>
	);
};

export default Login;
