			skinVariants={skinVariants}
			onBack={handleBack}
			onClose={handleClose}
		>
			{isLoginOpen ? (
				<Login onClose={handleLoginClose} />
			) : (
				<Main />
			) : (
				<Signup onLoginClick={handleGoToLogin} onSignup={handleSignup} />
			)}

