	);
};

export default ThemeDecorator(App); */


const App = () => {
    const [currentPage, setCurrentPage] = useState('login'); // 현재 페이지 상태
    //const [userInfo, setUserInfo] = useState({ username: '', password: '' }); // 회원 정보 저장

    const goToSignup = () => setCurrentPage('signup'); // 회원가입 화면으로 이동
    const goToLogin = () => setCurrentPage('login');   // 로그인 화면으로 이동
    const goToMain = () => setCurrentPage('main');     // 메인 화면으로 이동
    const handleSignup = (username, password) => {
        setUserInfo({ username, password });
        setCurrentPage('login'); // 회원가입 완료 후 로그인 화면으로 이동
    };

    return (
        <div>
            {currentPage === 'login' && (
                <Login
                    onClose={goToMain}
                    onSignupClick={goToSignup} // 미리 정의된 함수 사용
                />
            )}
            {currentPage === 'signup' && (
                <Signup
                    onLoginClick={goToLogin} // 미리 정의된 함수 사용
                    onSignup={handleSignup} // 미리 정의된 함수 사용
                />
            )}
            {currentPage === 'main' && <Main />}
        </div>
    );
};
