// src/views/Login.js 
import BodyText from '@enact/sandstone/BodyText';
import {Header, Panel} from '@enact/sandstone/Panels';
import $L from '@enact/i18n/$L';
import Button from '@enact/sandstone/Button';
import Input from '@enact/sandstone/Input';
import css from './Login.module.less';
import TabLayout from '@enact/sandstone/TabLayout';

const Login = ({ props, onClose, onSubmit, username, password, handleUsernameChange, handlePasswordChange }) => {
    return(
        <Panel {...props} >
            <Header title={$L('Log In and Sign Up')}/>
            <TabLayout>
                <Tab title = {$L('Log In')}>
                    <div className={css.loginContainer}>
                        <BodyText className={css.title}>Login</BodyText>
                        <Input
                            placeholder="Username"
                            value={username}
                            onChange={handleUsernameChange}
                            type="text"
                            className={css.input}
                        />
                        <Input
                            placeholder="Password"
                            value={password}
                            onChange={handlePasswordChange}
                            type="password"
                            className={css.input}
                        />
                        <Button onClick={onSubmit} size="small" className={css.button}>
                            Login
                        </Button>
                        <Button onClick={onClose} size="small" className={css.button}>
                            Cancel
                        </Button>
                    </div>
                </Tab>
            </TabLayout>

        </Panel>
    )
}

export default Login;
