// src/App.js
import { useState } from 'react';
import ThemeDecorator from '@enact/sandstone/ThemeDecorator';
import Panels from '@enact/sandstone/Panels';
import Main from '../views/Main';
import { useBackHandler, useCloseHandler, useDocumentEvent } from './AppState';
import { isDevServe } from '../libs/utils';
import Login from '../views/Login';
import { useLogin } from '../views/LoginState';
import Signup from '../views/Signup';

/* istanbul ignore next*/
if (isDevServe()) {
	window.webOSSystem = {
		highContrast: 'off',
		close: () => {},
		platformBack: () => {},
		PmLogString: () => {},
		screenOrientation: 'landscape',
		setWindowOrientation: () => {}
	};
}

const renderContent = () => {
	switch (currentView) {
		case 'login':
			return <Login onClose={handleLoginClose} onSignup={handleSignup} />;
		case 'signup':
			return <Signup onLoginClick={handleLoginClose} />;
		case 'main':
		default:
			return <Main />;
	}
};

const App = (props) => {
	const [skinVariants, setSkinVariants] = useState({ highContrast: false });
	const handleBack = useBackHandler();
	const handleClose = useCloseHandler();
	const { isLoginOpen, isSignupOpen, handleLoginClose, handleSignup } = useLogin();
	useDocumentEvent(setSkinVariants);

	return (
		<Panels
			{...props}
			skinVariants={skinVariants}
			onBack={handleBack}
			onClose={handleClose}
		>	

			{renderContent()}

			{/*{isLoginOpen ? (
				<Login onClose={handleLoginClose} onSignup = {handleSignup} />
			) : isSignupOpen ? (
				<Signup onLoginClick={handleLoginClose} />
			) :	(
				<Main />
			)}*/}
		</Panels>
	);
};

export default ThemeDecorator(App);
