import { useState } from 'react';
import ThemeDecorator from '@enact/sandstone/ThemeDecorator';
import Panels from '@enact/sandstone/Panels';
import Alert from '@enact/sandstone/Alert';
import BodyText from '@enact/sandstone/BodyText';
import Button from '@enact/sandstone/Button';
import { Header, Panel } from '@enact/sandstone/Panels';
import { useBackHandler, useCloseHandler, useDocumentEvent } from './AppState';
import { isDevServe } from '../libs/utils';
import Login from '../views/Login';
import { usePopup } from './MainState';
import { useLogin } from '../App/login/LoginState';
import css from './Main.module.less';
import $L from '@enact/i18n/$L';

if (isDevServe()) {
    window.webOSSystem = {
        highContrast: 'off',
        close: () => {},
        platformBack: () => {},
        PmLogString: () => {},
        screenOrientation: 'landscape',
        setWindowOrientation: () => {}
    };
}

const App = props => {
    const [skinVariants, setSkinVariants] = useState({ highContrast: false });
    const handleBack = useBackHandler();
    const handleClose = useCloseHandler();
    useDocumentEvent(setSkinVariants);

    const { isPopupOpen, handlePopupOpen, handlePopupClose, handleLaunchApp } = usePopup();
    const {
        isLoginOpen,
        handleLoginOpen,
        handleLoginClose,
        handleLogin,
        handleUsernameChange,
        handlePasswordChange,
        username,
        password
    } = useLogin();

    return (
        <Panels
            {...props}
            skinVariants={skinVariants}
            onBack={handleBack}
            onClose={handleClose}
        >
            <Panel>
                {isLoginOpen ? (
                    <Login
                        isOpen={isLoginOpen}
                        onClose={handleLoginClose}
                        onSubmit={handleLogin}
                        handleUsernameChange={handleUsernameChange}
                        handlePasswordChange={handlePasswordChange}
                        username={username}
                        password={password}
                    />
                ) : (
                    <>
                        <Header title={$L('Enact Template')} />
                        <BodyText>{$L('This is a main page of sample application.')}</BodyText>

                        <Button onClick={handlePopupOpen} size="small" className={css.buttonCell}>
                            {$L('Open Alert')}
                        </Button>
                        <Alert type="overlay" open={isPopupOpen} onClose={handlePopupClose}>
                            <span>{$L('This is an alert message.')}</span>
                            <buttons>
                                <Button
                                    size="small"
                                    className={css.buttonCell}
                                    onClick={handleLaunchApp}
                                >
                                    Launch
                                </Button>
                                <Button
                                    size="small"
                                    className={css.buttonCell}
                                    onClick={handlePopupClose}
                                >
                                    {$L('Close')}
                                </Button>
                            </buttons>
                        </Alert>

                        <Button onClick={handleLoginOpen} size="small" className={css.buttonCell}>
                            {$L('Login')}
                        </Button>
                    </>
                )}
            </Panel>
        </Panels>
    );
};

export default ThemeDecorator(App);
