import { useState, useCallback } from 'react';
import { useHistory } from 'react-router-dom'; // react-router-dom 사용
import debugLog from '../libs/log';

export const useLoginState = (props) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const history = useHistory(); // 히스토리 객체를 사용하여 페이지 이동

    const handleInputChange = useCallback((event) => {
        const { name, value } = event.target;
        if (name === 'username') {
            setUsername(value);
        } else if (name === 'password') {
            setPassword(value);
        }
    }, []);

    const handleLogin = useCallback(() => {
        // 로그인 버튼 클릭 시 메인 화면으로 이동
        debugLog('로그인 버튼 클릭');
        history.push('/main'); // 메인 화면으로 이동
    }, [history]);

    return { username, password, handleInputChange, handleLogin };
};
