// src/views/LoginState.js
import { useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom'; // useNavigate import
import debugLog from '../libs/log';

export const useLoginState = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate(); // 페이지 이동을 위한 navigate 객체

    const handleInputChange = useCallback((event) => {
        const { name, value } = event.target;
        if (name === 'username') {
            setUsername(value); // 사용자 이름 상태 업데이트
        } else if (name === 'password') {
            setPassword(value); // 비밀번호 상태 업데이트
        }
    }, [setUsername]);

    // 로그인 처리 함수
    const handleLogin = useCallback(() => {
        debugLog('로그인 시도:', username, password); // 로그인 시도 로그
        // 로그인 성공 후 메인 페이지로 이동
        navigate('/main');
    }, [navigate, username, password]);

    // 회원가입 페이지로 이동하는 함수
    const handleNavigateToSignup = useCallback(() => {
        navigate('/signup');
    }, [navigate]);

    return { username, password, handleInputChange, handleLogin, handleNavigateToSignup };
};
