// src/App.js
import { useState } from 'react';
import ThemeDecorator from '@enact/sandstone/ThemeDecorator';
import Panels from '@enact/sandstone/Panels';
import Main from '../views/Main';
import VideoPlayer from '../views/VideoPlayer';
import { useBackHandler, useCloseHandler, useDocumentEvent } from './AppState';
import { isDevServe } from '../libs/utils';
import Login from '../views/Login';
import { useLogin } from '../views/LoginState';

/* istanbul ignore next*/
if (isDevServe()) {
	window.webOSSystem = {
		highContrast: 'off',
		close: () => {},
		platformBack: () => {},
		PmLogString: () => {},
		screenOrientation: 'landscape',
		setWindowOrientation: () => {}
	};
}

const App = (props) => {
	const [skinVariants, setSkinVariants] = useState({ highContrast: false });
	const [currentView, setCurrentView] = useState('main'); // 현재 뷰 상태 추가
	const [currentVideoUrl, setCurrentVideoUrl] = useState(''); // 현재 비디오 URL 상태 추가
	const handleBack = useBackHandler();
	const handleClose = useCloseHandler();
	const { isLoginOpen, handleLoginClose } = useLogin();
	useDocumentEvent(setSkinVariants);

	const handleNavigateToVideo = (videoUrl) => {
		setCurrentVideoUrl(videoUrl); // 선택한 비디오 URL 저장
		setCurrentView('video'); // 비디오 뷰로 변경
	};

	const handleNavigateToMain = () => {
		setCurrentView('main'); // 메인 뷰로 변경
	};

	return (
		<Panels
			{...props}
			skinVariants={skinVariants}
			onBack={handleBack}
			onClose={handleClose}
		>
			{isLoginOpen ? (
				<Login onClose={handleLoginClose} />
			) : currentView === 'video' ? (
				<VideoPlayer videoUrl={currentVideoUrl} onBack={handleNavigateToMain} />
			) : (
				<Main onNavigateToVideo={handleNavigateToVideo} />
			)}
		</Panels>
	);
};

export default ThemeDecorator(App);
