import React, { useState } from 'react';
import BodyText from '@enact/sandstone/BodyText';
import { Header, Panel } from '@enact/sandstone/Panels';
import { Input } from '@enact/sandstone/Input'; // Enact Input 컴포넌트
import { Button } from '@enact/sandstone/Button'; // Enact Button 컴포넌트
import css from './Login.module.less';
import $L from '@enact/i18n/$L';

const Login = ({ handleLogin }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        if (name === 'username') {
            setUsername(value);
        } else if (name === 'password') {
            setPassword(value);
        }
    };

    const login = () => {
        // 여기에 로그인 로직을 추가합니다.
        // 예: 아이디와 비밀번호를 확인하고, 성공 시 handleLogin 호출
        if (username && password) {
            handleLogin(); // 로그인 성공 처리
        } else {
            alert('사용자 이름과 비밀번호를 입력하세요.');
        }
    };

    return (
        <Panel>
            <Header title={$L('로그인')} />
            <div className={css.loginContainer}>
                <BodyText>{$L('로그인하여 계속 진행하세요.')}</BodyText>
                <Input
                    type="text"
                    name="username"
                    placeholder={$L('사용자 이름')}
                    value={username} // 상태와 연결
                    onChange={handleInputChange} // 핸들러와 연결
                    className={css.input}
                />
                <Input
                    type="password"
                    name="password"
                    placeholder={$L('비밀번호')}
                    value={password} // 상태와 연결
                    onChange={handleInputChange} // 핸들러와 연결
                    className={css.input}
                />
                <Button onClick={login} className={css.loginButton}>
                    {$L('로그인')}
                </Button>
                {/* 회원가입 버튼이 필요 없다면 주석 처리하거나 삭제하세요 */}
                {/* <Button onClick={handleNavigateToSignup} className={css.signupButton}>
                    {$L('회원가입')}
                </Button> */}
            </div>
        </Panel>
    );
};

export default Login;
