import Alert from '@enact/sandstone/Alert';
import BodyText from '@enact/sandstone/BodyText';
import Button from '@enact/sandstone/Button';
import Input from '@enact/sandstone/Input';
import {Header, Panel} from '@enact/sandstone/Panels';
import {usePopup} from './MainState';

import css from './Login.module.less';
import $L from '@enact/i18n/$L';
import {useState} from 'react';

const Login = (props) => {
	const {isPopupOpen, handlePopupOpen, handlePopupClose} = usePopup();
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');

	const handleLogin = () => {
		if (email && password) {
			handlePopupOpen(); // 로그인 성공 메시지 팝업 열기
		} else {
			alert($L('Please enter your email and password.')); // 입력 확인
		}
	};

	return (
		<Panel {...props}>
			<Header title={$L('Login')} />
			<BodyText>{$L('Please enter your credentials to log in.')}</BodyText>
			<Input
				placeholder={$L('Email')}
				value={email}
				onChange={e => setEmail(e.value)}
				className={css.inputField}
			/>
			<Input
				placeholder={$L('Password')}
				type="password"
				value={password}
				onChange={e => setPassword(e.value)}
				className={css.inputField}
			/>
			<Button onClick={handleLogin} size="small" className={css.buttonCell}>
				{$L('Login')}
			</Button>
			<Alert type="overlay" open={isPopupOpen} onClose={handlePopupClose}>
				<span>{$L('Login successful!')}</span>
				<Button
					size="small"
					className={css.buttonCell}
					onClick={handlePopupClose}
				>
					{$L('Close')}
				</Button>
			</Alert>
		</Panel>
	);
};

export default Login;
