// AppState.js

import { useEffect } from 'react';

export function useBackHandler() {
    function handleBack() {
        // 뒤로가기 동작 정의
        console.log('Back button pressed');
    }
    return handleBack;
}

export function useCloseHandler() {
    function handleClose() {
        // 닫기 동작 정의
        console.log('Close button pressed');
    }
    return handleClose;
}

export function useDocumentEvent(setSkinVariants) {
    useEffect(function() {
        function handleDocumentEvent() {
            // 문서 이벤트 처리
            setSkinVariants(function(prev) {
                return { ...prev, highContrast: !prev.highContrast };
            });
        }

        document.addEventListener('someEvent', handleDocumentEvent);
        return function() {
            document.removeEventListener('someEvent', handleDocumentEvent);
        };
    }, [setSkinVariants]);
}
