// src/views/Login.js
import BodyText from '@enact/sandstone/BodyText';
import { Header, Panel } from '@enact/sandstone/Panels';
import { Input } from '@enact/sandstone/Input'; // Enact Input 컴포넌트
import { Button } from '@enact/sandstone/Button'; // Enact Button 컴포넌트
import { useLoginState } from './LoginState';
import css from './Login.module.less';
import $L from '@enact/i18n/$L';
import { useNavigate } from 'react-router-dom'; // useNavigate import

const Login = () => {
    const { username, password, handleInputChange, handleLogin } = useLoginState();
    const navigate = useNavigate(); // navigate 훅을 사용하여 페이지 이동 기능 추가

    // 회원가입 페이지로 이동하는 함수
    const handleNavigateToSignup = () => {
        navigate('/signup');
    };

    return (
        <Panel>
            <Header title={$L('로그인')} />
            <div className={css.loginContainer}>
                <BodyText>{$L('로그인하여 계속 진행하세요.')}</BodyText>
                <Input
                    type="text"
                    name="username"
                    placeholder={$L('사용자 이름')}
                    value={username}
                    onChange={handleInputChange}
                    className={css.inputField}
                />
                <Input
                    type="password"
                    name="password"
                    placeholder={$L('비밀번호')}
                    value={password}
                    onChange={handleInputChange}
                    className={css.inputField}
                />
                <Button onClick={handleLogin} className={css.loginButton}>
                    {$L('로그인')}
                </Button>
                <Button onClick={handleNavigateToSignup} className={css.signupButton}>
                    {$L('회원가입')}
                </Button>
            </div>
        </Panel>
    );
};

export default Login;
